uniform vec3 ps;uniform vec4 so;uniform vec4 es;uniform vec4 ms;uniform vec3 ls;uniform float pp;uniform vec3 mo;varying vec2 cg;varying vec3 hg;varying vec3 cd;varying vec4 gg;const vec3 a=vec3(1.0,1.0,1.0);const vec3 b=vec3(1.0,1.0,1.0);const vec3 c=vec3(0.4,0.4,0.4);void main(){vec3 d=normalize(cd-ls);vec3 e=normalize(hg);vec3 f=normalize(reflect(d,e));float g=max(dot(f,ps),.001);vec3 h=c*ms.rgb*ms.w*pow(g,16.0);vec3 i=a*es.rgb*es.w*clamp(dot(e,ps),.0,1.0);vec3 j=b*so.rgb*so.w;vec3 k=vec3(j+i+h);float l=abs(cg.x-0.5)/0.5;gl_FragColor=(1.0-l)*vec4(mo,1.0)+l*gg;gl_FragColor*=vec4(k,1.0);float m=pp*0.01;gl_FragColor.w=m;}

attribute vec3 $position;attribute vec3 $auxpos1;uniform mat4 ss;uniform mat4 sm;uniform bool ol;uniform float sl;uniform sampler2D om;uniform sampler2D ie;uniform vec2 im;uniform float oe;uniform vec2 oi;uniform float os;uniform vec2 em;uniform bool is;uniform sampler2D ee;varying vec3 hg;varying vec3 cd;varying vec2 cg;varying vec4 gg; $lonlat2position  $translationMat  $linearInterpolator  $localTrans  $expoEaseOut const float a=0.0;const float b=1.0;const float c=2.0;const float d=3.0;void main(void){float e=fract($auxpos1.x/im.x);float f=floor($auxpos1.x/im.y)/im.y;float g=(texture2D(om,vec2(e,f))).r;float h=(texture2D(ie,vec2(e,f))).r;float i=mod(os,oe);float j=getExpoEaseOutValue(i,g,h,oe);if(is) j=h;float k=getLinearValue(em,j);if(ol){float l=200000.0;if($auxpos1.y==a||$auxpos1.y==d){l=getScope(oi,k);}else if($auxpos1.y==b||$auxpos1.y==c){l=1.25*getScope(oi,k);}if($auxpos1.y==a) cg=vec2(0.0,0.0);else if($auxpos1.y==d) cg=vec2(0.0,1.0);else if($auxpos1.y==b) cg=vec2(1.0,0.0);else if($auxpos1.y==c) cg=vec2(1.0,1.0);float m=$auxpos1.z*2.0*PI;vec3 n=vec3(l*cos(m),l*sin(m),0.0);vec3 o,p;mat4 q=mat4(1.0);
#ifdef GLOBAL
mat4 r=getTransMat($position);q=toRotationMat(r);o=(r*vec4(n,1.0)).xyz;vec4 s=vec4(0.0,0.0,1.0,0.0);p=(q*s).xyz;
#else
o=wgs84ToWebMerc($position);o+=n;p=vec3(1.0,0.0,1.0);
#endif
gl_Position=ss*sm*vec4(o,1.0);hg=normalize(p);cd=o;gg=texture2D(ee,vec2(k,0.5));}else{float t=getScope(oi,k);mat4 r=getTransMat($position);float u=($auxpos1.y-sl/2.0)*(20.0/sl);float v=($auxpos1.z-sl/2.0)*(20.0/sl);float w=exp(-0.0831*(u*u+v*v));float x=atan(u,v);u=w*cos(2.0*x);v=w*sin(2.0*x);float y=sqrt(u*u+v*v)*getScope(oi,k);vec3 o=(r*vec4(t*($auxpos1.y/sl-0.5),t*($auxpos1.z/sl-0.5),y,1.0)).xyz;vec3 p=normalize(o);gl_Position=ss*sm*vec4(o,1.0);hg=p;cd=o;cg=$auxpos1.yz/(sl-1.0);}}
